import React, { useState } from 'react';
import  io  from 'socket.io-client';
import Chat from './components/customers/Chat.js'
import './App.css'
import { Container } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'



const socket = io.connect("http://localhost:3001");

function App() {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);


  const joinRoom = () => {
    if (username !== "" && room !== "") {
      socket.emit("join_room", room);
      setShowChat(true)
    }
  }

  return (
    <div className="App">

      {!showChat ? (
        <Container className="border p-5 login-container">
          <div className="row mb-2">
          <h3 className="text-center">Join A Chat</h3>
          </div>
          <div class="input-group mb-3">
          <span class="input-group-text" id="basic-addon1">
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-person-fill" viewBox="0 0 16 16">
  <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z"/>
</svg>

          </span>
          <input
            className="form-control form-control-lg"
            type="text"
            placeholder="Username..."
            onChange={(event) => {
              setUsername(event.target.value);
            }}
          />
          </div>
          <div class="input-group mb-3">
          <span class="input-group-text" id="basic-addon1">ðŸ”‘</span>
          <input
            className="form-control form-control-lg"
            type="text"
            placeholder="Room ID..."
            onChange={(event) => {
              setRoom(event.target.value);
            }}
          />
          </div>
          <div className="row">
          <button className="btn btn-success fs-3" onClick={joinRoom}>Join A Room</button>
          </div>
        </Container>
      ) : (
        <Chat socket={socket} username={username} room={room} />
      )}

    </div>
  );
}

export default App;
