import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";

//Chat Component
function Chat({ socket, username, room }) {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        room: room,
        author: username,
        message: currentMessage,
        time:
          new Date(Date.now()).getHours() +
          ":" +
          new Date(Date.now()).getMinutes(),
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
  }, [socket]);

  return (
    <Container fluid className="chat-window px-2 bg-dark text-light">
      <div className="row mb-3 chat-header bg-dark text-light">
        <div className="col-12 col-lg-10">
          <h1 className="text-center">TALKANGER</h1>
        </div>
        <div className="col-auto align-items-center text-center">
          <h5>{`Current Room: `}</h5>
        </div>
      </div>

      <div className="row chat-body">
        <div className="message-container">
          {messageList.map((messageContent) => {
            return (
              <div
                className="message"
                id={username === messageContent.author ? "you" : "other"}
              >
                <div>
                  <div className="row gx-0">
                    <div className="col-1 user-picture">
                      <img
                        className="rounded bg-light user-image"
                        src={`https://avatars.dicebear.com/api/bottts/:${username}.svg`}
                        alt={`${username}'s Profile Character`}
                      />
                    </div>

                    <div className="col text-start message-content">
                      <p>{messageContent.message}</p>
                    </div>

                  </div>

                    <div className=" row message-meta">
                      <div className="col text-secondary">
                        <p id="author"> <strong>{messageContent.author}</strong> at {messageContent.time}</p>
                      </div>
                </div>
              </div>
            </div>
            );
          })}
        </div>
      </div>
      <div className="row g-0 chat-footer">
        <div className="col-11">
          <input
            className="form-control w-100"
            type="text"
            value={currentMessage}
            placeholder="Hey..."
            onChange={(event) => {
              setCurrentMessage(event.target.value);
            }}
            onKeyPress={(event) => {
              event.key === "Enter" && sendMessage();
            }}
          />
        </div>
        <div className="col">
          <button className="btn btn-dark send-message w-100" onClick={sendMessage}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="25"
              height="25"
              fill="currentColor"
              class="bi bi-arrow-right-circle"
              viewBox="0 0 16 16"
            >
              <path
                fill-rule="evenodd"
                d="M1 8a7 7 0 1 0 14 0A7 7 0 0 0 1 8zm15 0A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM4.5 7.5a.5.5 0 0 0 0 1h5.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3a.5.5 0 0 0 0-.708l-3-3a.5.5 0 1 0-.708.708L10.293 7.5H4.5z"
              />
            </svg>
          </button>
        </div>
      </div>
    </Container>
  );
}

export default Chat;
