import React, { useState } from 'react';
import  io  from 'socket.io-client';
import Chat from './components/customers/Chat.js'
import './App.css'
import { Container } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'



const socket = io.connect("http://localhost:3001");

function App() {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);


  const joinRoom = () => {
    if (username !== "" && room !== "") {
      socket.emit("join_room", room);
      setShowChat(true)
    }
  }

  return (
    <div className="App">
      <div class="slider-thumb">
      {!showChat ? (
        <Container className="border p-5">
          <div className="row mb-2">
          <h3 className="text-center">Join A Chat</h3>
          </div>
          <div class="input-group mb-3">
          <span class="input-group-text" id="basic-addon1">ü¶∏‚Äç‚ôÇÔ∏è</span>
          <input
            className="form-control form-control-lg"
            type="text"
            placeholder="Username..."
            onChange={(event) => {
              setUsername(event.target.value);
            }}
          />
          </div>
          <div class="input-group mb-3">
          <span class="input-group-text" id="basic-addon1">üîë</span>
          <input
            className="form-control form-control-lg"
            type="text"
            placeholder="Room ID..."
            onChange={(event) => {
              setRoom(event.target.value);
            }}
          />
          </div>
          <div className="row">
          <button className="btn btn-success fs-3" onClick={joinRoom}>Join A Room</button>
          </div>
        </Container>
      ) : (
        <Chat socket={socket} username={username} room={room} />
      )}
      </div>
    </div>
  );
}

export default App;
